const {options, dumps} = require('./config')

const restoreDBQuestions = [{
  type: 'input',
  name: 'host',
  message: 'Host name',
  default: options.hostname
},
  {
    type: 'input',
    name: 'port',
    message: 'Host port',
    default: options.portnumber
  },
  {
    type: 'list',
    name: 'force',
    message: 'Force restore?',
    choices: ['Yes', 'No']
  },
  {
    type: 'list',
    name: 'filename',
    message: 'Choose dump file name',
    choices: dumps
  },
  {
    type: 'input',
    name: 'db',
    message: 'Dump file database name',
    default: options.db,
    validate: (input) => {
      return new Promise((resolve, reject) => {
        if (!input.length) {
          reject('Please provide a database name!')
        }
        resolve(true)
      })
    }
  },
]

module.exports = restoreDBQuestions
